class StoredConstants{constructor(){this.constants={},this.load()}add(name="",value=""){this.constants.hasOwnProperty(name)?alert("Eine Konstante mit diesem Namen existiert bereits"):this.constants[name]=value}remove(name=""){delete this.constants[name],this.save()}value(name=""){return this.constants.hasOwnProperty(name)?this.constants[name]:""}save(){localStorage.setItem("calculator-stored-constants",JSON.stringify(this.constants))}load(){let con=localStorage.getItem("calculator-stored-constants");null!==con&&(this.constants=JSON.parse(con))}}